(54, 'kulturas_piemineklis', 'yes', '\\b(kulturas\\s+pieminekl\\w*|pieminekl\\w*\\s*—\\s*eka|valsts\\s+aizsargajam\\w*\\s+kulturas\\s+pieminekl\\w*|pieminekla\\s+teritorij\\w*)\\b', 'regex', 5, 1, 'Kultūras piemineklis / aizsargājamā teritorija', '2026-01-20 18:24:00'),
(55, 'vieta', 'pilseta', '\\b(pilset\\w*|pilseta|pilsetas)\\b', 'regex', 30, 1, 'Atrašanās: pilsētā', '2026-01-20 18:24:00'),
(56, 'vieta', 'arpus_pilsetas', '\\b(arpus\\s+pilset\\w*|lauku\\s+teritorij\\w*|ne\\s*pilset\\w*)\\b', 'regex', 20, 1, 'Atrašanās: ārpus pilsētas', '2026-01-20 18:24:00'),
(57, 'vieta', 'publiska_artelpa', '\\b(publisk\\w*\\s+artelp\\w*|publisk\\w*\\s+viet\\w*|publisk\\w*\\s+teritorij\\w*)\\b', 'regex', 20, 1, 'Atrašanās: publiskā ārtelpā', '2026-01-20 18:24:00'),
(58, 'darbu_veids', 'interjera_projekts', '\\b(interjer\\w*\\s+projekt\\w*|interjer\\w*)\\b', 'regex', 10, 1, '7.1 Interjera projekts (pats par sevi)', '2026-01-20 18:24:00'),
(59, 'darbu_veids', 'fasades_jumta_krasosana', '\\b(fasad\\w*\\s+kr[āa]so\\w*|jumta\\s+iesegum\\w*\\s+kr[āa]so\\w*|kr[āa]so\\w*\\s+fasad\\w*|kr[āa]so\\w*\\s+jumt\\w*)\\b', 'regex', 10, 1, '7.2 Fasādes/jumta ieseguma krāsošana', '2026-01-20 18:24:00'),
(60, 'darbu_veids', 'fasades_apgaismojums', '\\b(fasad\\w*\\s+apgaismojum\\w*|apgaismojum\\w*\\s+ierikos\\w*)\\b', 'regex', 15, 1, '7.2 Fasādes apgaismojuma ierīkošana', '2026-01-20 18:24:00'),
(61, 'darbu_veids', 'markizes_un_ierices', '\\b(markiz\\w*|izvieto\\w*\\s+uz\\s+fasad\\w*|ieric\\w*\\s+uz\\s+fasad\\w*|ieric\\w*\\s+uz\\s+jumt\\w*)\\b', 'regex', 20, 1, '7.2 Markīzes / ierīces uz fasādes/jumta', '2026-01-20 18:24:00'),
