(63, 'vieta', 'meza_zeme', '\\b(meza\\s+zem\\w*|meza\\s+teritorij\\w*)\\b', 'regex', 15, 1, '7.3.3 Meža zemes', '2026-01-20 18:24:00'),
(64, 'darbu_veids', 'logu_atjaunosana_nomaina', '\\b(log\\w*\\s+(atjauno\\w*|nomai\\w*)|loga\\s+dalijum\\w*|loga\\s+proporcij\\w*|ramj\\w*\\s+kr[āa]so\\w*)\\b', 'regex', 10, 1, '7.4.3 Logu atjaunošana/nomaiņa (dalījums/proporcijas/krāsojums)', '2026-01-20 18:24:00'),
(65, 'darbu_veids', 'ardurvju_atjaunosana_nomaina', '\\b(ardurv\\w*\\s+(atjauno\\w*|nomai\\w*)|durvju\\s+dalijum\\w*|durvju\\s+proporcij\\w*)\\b', 'regex', 12, 1, '7.4.4 Ārdurvju atjaunošana/nomaiņa', '2026-01-20 18:24:00'),
(66, 'darbu_veids', 'apmale_lievenis_kapnes_panduss_terase_jumtudeni', '\\b(apmal\\w*|lieven\\w*|arej\\w*\\s+kapn\\w*|pandus\\w*|teras\\w*\\s+zemes\\s+limen\\w*|jumtudenu\\s+novad\\w*|notekudenu\\s+novad\\w*)\\b', 'regex', 25, 1, '7.4.5 Apmales/lieveņi/kāpnes/panduss/terase/jumtūdeņu novadsistēmas', '2026-01-20 18:24:00'),
(67, 'darbu_veids', 'jumta_ieseguma_atjaunosana_nomaina', '\\b(jumta\\s+iesegum\\w*\\s+(atjauno\\w*|nomai\\w*)|jumta\\s+segum\\w*\\s+(atjauno\\w*|nomai\\w*))\\b', 'regex', 20, 1, '7.4.6 Jumta ieseguma atjaunošana/nomaiņa', '2026-01-20 18:24:00'),
