(69, 'darbu_veids', 'publisks_pasakums_ielu_tirdznieciba', '\\b(publisk\\w*\\s+pasakum\\w*|ielu\\s+tirdzniecib\\w*|tirdzniecib\\w*\\s+atla\\w*|pasakum\\w*\\s+atla\\w*)\\b', 'regex', 15, 1, '7.5 Publisks pasākums / ielu tirdzniecība (atļauja)', '2026-01-20 18:24:00');
INSERT INTO `embpd_intent_rules` (`id`, `intent`, `rule_type`, `pattern`, `action`, `action_priority`, `scope`, `enabled`, `priority`, `note`) VALUES
(19, 'eka', 'contains', 'dzivojama eka', 'Ja jautājums attiecas uz ēku, pamatojumu un citātus prioritāri meklē Ēku būvnoteikumos. Atsevišķu inženierbūvju būvnoteikumus neizmanto, ja vien lietotājs nepārprotami nav norādījis, ka būve ir inženierbūve.', 5, 'system', 1, 10, 'ēka: dzīvojamā ēka'),
(20, 'eka', 'contains', 'dzivojamo eka', 'Ja jautājums attiecas uz ēku, pamatojumu un citātus prioritāri meklē Ēku būvnoteikumos. Atsevišķu inženierbūvju būvnoteikumus neizmanto, ja vien lietotājs nepārprotami nav norādījis, ka būve ir inženierbūve.', 5, 'system', 1, 20, 'ēka: dzīvojamā ēka'),
(21, 'eka', 'contains', 'eka', 'Ja jautājums attiecas uz ēku, pamatojumu un citātus prioritāri meklē Ēku būvnoteikumos. Atsevišķu inženierbūvju būvnoteikumus neizmanto, ja vien lietotājs nepārprotami nav norādījis, ka būve ir inženierbūve.', 6, 'system', 1, 50, 'ēka vispārīgi'),
(22, 'eka', 'regex', '\\bmaj\\w*\\b', 'Ja jautājums attiecas uz ēku, pamatojumu un citātus prioritāri meklē Ēku būvnoteikumos. Atsevišķu inženierbūvju būvnoteikumus neizmanto, ja vien lietotājs nepārprotami nav norādījis, ka būve ir inženierbūve.', 7, 'system', 1, 60, 'māja, mājas...'),
(23, 'eka', 'contains', 'privatmaja', 'Ja jautājums attiecas uz ēku, pamatojumu un citātus prioritāri meklē Ēku būvnoteikumos.', 8, 'system', 1, 70, 'privātmāja'),
(24, 'eka', 'contains', 'viena dzivokla', 'Ja jautājums attiecas uz ēku, pamatojumu un citātus prioritāri meklē Ēku būvnoteikumos.', 9, 'system', 1, 80, 'viena dzīvokļa ēka'),
(25, 'inzenierbuve', 'contains', 'inzenierbuve', 'Ja jautājums attiecas uz inženierbūvi, pamatojumu un citātus prioritāri meklē Atsevišķu inženierbūvju būvnoteikumos. Ēku būvnoteikumus izmanto tikai, ja tie konkrētajā situācijā ir tieši piemērojami.', 5, 'system', 1, 10, 'inženierbūve'),
(26, 'inzenierbuve', 'contains', 'inzenierbuv', 'Ja jautājums attiecas uz inženierbūvi, pamatojumu un citātus prioritāri meklē Atsevišķu inženierbūvju būvnoteikumos. Ēku būvnoteikumus izmanto tikai, ja tie konkrētajā situācijā ir tieši piemērojami.', 6, 'system', 1, 20, 'inženierbūves sakne'),
(27, 'iecere', 'contains', 'bis', 'Ja lietotājs jautā par būvniecības ieceri BIS vai kādu dokumentu iesniegt būvvaldē (paskaidrojuma raksts, apliecinājuma karte, būvatļauja), pamatojumu un citātu primāri meklē Ēku būvnoteikumu 7. nodaļā. Tikai ja 7. nodaļā nav atbildes, izmanto citas nodaļas.', 1, 'system', 1, 10, 'BIS -> EBN 7. nodaļa'),
(28, 'iecere', 'contains', 'iecere', 'Ja lietotājs jautā par būvniecības ieceri vai ieceres dokumentāciju, pamatojumu un citātu primāri meklē Ēku būvnoteikumu 7. nodaļā.', 2, 'system', 1, 20, 'Iecere -> EBN 7. nodaļa'),
(29, 'iecere', 'contains', 'iesniegt', 'Ja lietotājs jautā par būvniecības ieceres iesniegšanu BIS/būvvaldē, pamatojumu un citātu primāri meklē Ēku būvnoteikumu 7. nodaļā.', 3, 'system', 1, 30, 'iesniegt -> EBN 7. nodaļa'),
(30, 'iecere', 'contains', 'dokuments', 'Ja lietotājs jautā par būvniecības ieceres dokumentiem/dokumentāciju, pamatojumu un citātu primāri meklē Ēku būvnoteikumu 7. nodaļā.', 3, 'system', 1, 40, 'dokumenti -> EBN 7. nodaļa'),
(31, 'iecere', 'contains', 'paskaidrojuma rakst', 'Paskaidrojuma raksta, apliecinājuma kartes vai būvatļaujas izvēlei primāri izmanto Ēku būvnoteikumu 7. nodaļu.', 4, 'system', 1, 50, 'PR/AK/BL -> EBN 7. nodaļa'),
(32, 'iecere', 'contains', 'apliecinajuma kart', 'Paskaidrojuma raksta, apliecinājuma kartes vai būvatļaujas izvēlei primāri izmanto Ēku būvnoteikumu 7. nodaļu.', 4, 'system', 1, 60, 'AK -> EBN 7. nodaļa'),
(33, 'iecere', 'contains', 'buvatlauja', 'Paskaidrojuma raksta, apliecinājuma kartes vai būvatļaujas izvēlei primāri izmanto Ēku būvnoteikumu 7. nodaļu.', 4, 'system', 1, 70, 'BL -> EBN 7. nodaļa');
