(78, 3, 'assistant', '⚠️ Kļūda: OpenAI error 429: {\n    \"error\": {\n        \"message\": \"Request too large for gpt-4o in organization org-ZqfjPny9cgmcZUk6MiaCSxKK on tokens per min (TPM): Limit 30000, Requested 110901. The input or output tokens must be reduced in order to run successfully. Visit https://platform.openai.com/account/rate-limits to learn more.\",\n        \"type\": \"tokens\",\n        \"param\": null,\n        \"code\": \"rate_limit_exceeded\"\n    }\n}\n', NULL, '2026-01-20 11:43:05'),
(79, 3, 'user', 'mūs datu bāzes tabulas\r\n--\r\n-- Table structure for table `embpd_apmaciba`\r\n--\r\n\r\nCREATE TABLE `embpd_apmaciba` (\r\n  `id` bigint(20) UNSIGNED NOT NULL,\r\n  `jautajums` text NOT NULL,\r\n  `gpt_atbilde` mediumtext NOT NULL,\r\n  `labota_atbilde` mediumtext DEFAULT NULL,\r\n  `der_apmacibai` tinyint(1) NOT NULL DEFAULT 0,\r\n  `datums` datetime NOT NULL DEFAULT current_timestamp(),\r\n  `forma` varchar(50) DEFAULT NULL,\r\n  `session_id` varchar(64) DEFAULT NULL,\r\n  `user_id` varchar(64) DEFAULT NULL,\r\n  `gpt_modelis` varchar(100) DEFAULT NULL,\r\n  `kvalitate` varchar(50) DEFAULT NULL,\r\n  `komentars` text DEFAULT NULL,\r\n  `created_at` datetime NOT NULL DEFAULT current_timestamp()\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;\r\n\r\n--\r\n-- Dumping data for table `embpd_apmaciba`\r\n--\r\n\r\nINSERT INTO `embpd_apmaciba` (`id`, `jautajums`, `gpt_atbilde`, `labota_atbilde`, `der_apmacibai`, `datums`, `forma`, `session_id`, `user_id`, `gpt_modelis`, `kvalitate`, `komentars`, `created_at`) VALUES\r\n(1, \'likums\', \'\\\"Likums\\\" ir valsts izdoti normatīvi akti, kas nosaka noteiktas kārtības, prasības, tiesības un pienākumus. Tas ir visaugstākās juridiskās varas izpausme, kas ir saistoša visiem valsts iedzīvotājiem un institūcijām. Likumu pieņem parlaments (Latvijā - Saeima), un tas stājas spēkā pēc publicēšanas oficiālajā publikācijā (Latvijā - \\\"Latvijas Vēstnesis\\\"). Likumu var mainīt, grozīt vai atcelt tikai ar citu likumu. Likumi ir daļa no valsts tiesību sistēmas un tie nosaka valsts un sabiedrības darbības pamatprincipus.\', \'\', 0, \'2025-12-17 00:00:00\', \'\', \'\', \'\', \'\', \'\', \'\', \'2026-01-17 10:13:01\'),', NULL, '2026-01-20 11:44:51'),
(80, 3, 'assistant', '⚠️ Kļūda: OpenAI error 429: {\n    \"error\": {\n        \"message\": \"Request too large for gpt-4o in organization org-ZqfjPny9cgmcZUk6MiaCSxKK on tokens per min (TPM): Limit 30000, Requested 111065. The input or output tokens must be reduced in order to run successfully. Visit https://platform.openai.com/account/rate-limits to learn more.\",\n        \"type\": \"tokens\",\n        \"param\": null,\n        \"code\": \"rate_limit_exceeded\"\n    }\n}\n', NULL, '2026-01-20 11:44:51'),
